import org.apache.batik.swing.JSVGScrollPane;
import org.apache.batik.swing.JSVGCanvas;
import java.awt.event.MouseWheelListener;
import java.awt.event.MouseWheelEvent;
import javax.swing.JScrollBar;
import java.awt.event.InputEvent;

public class JSVGScrollerWithMouseWheelListener extends JSVGScrollPane {
  public JSVGScrollerWithMouseWheelListener(JSVGCanvas c) {
    super(c);
    addMouseWheelListener(new WheelListener());
  }
  private class WheelListener implements MouseWheelListener {
    private WheelListener() {
      super();
    }
    public void mouseWheelMoved(MouseWheelEvent e) {
      final JScrollBar sb = (((e.getModifiers() & InputEvent.SHIFT_MASK) == 0) ? vertical : horizontal);
      if (sb.isVisible()) {
        if (e.getScrollType() == MouseWheelEvent.WHEEL_UNIT_SCROLL) {
          final int amt = e.getUnitsToScroll() * sb.getUnitIncrement();
          sb.setValue(sb.getValue() + amt);
        } else
        if (e.getScrollType() == MouseWheelEvent.WHEEL_BLOCK_SCROLL) {
          final int amt = e.getWheelRotation() * sb.getBlockIncrement();
          sb.setValue(sb.getValue() + amt);
        }
      }
    }
  }

}
